mapper.select:

/* 전체 블로그 포스팅 개수 조회 쿼리 */

select count(*)
from web_app_test.tb_blog_posting_info as tb_posting_info

mapper.insert:

/* 특정 포스팅 삽입 쿼리 (레코드의 차이가 하나일때 사용) */
insert into web_app_test.tb_blog_posting_info(f_t_no, title, img_src, posted_reg_dt)
value (#{postingTypeNo}, #{postingTitle}, #{postingImageSrc}, #{postingRegDt})


    /* 특정 포스팅 질문 삽입 쿼리 */
    select max(n_b_p_no)
    from web_app_test.tb_blog_posting_info


/* 포스팅 리스트 삽입 쿼리*/

insert into web_app_test.tb_blog_posting_info(
    f_t_no, title, img_src, posted_reg_dt
) values

(
    #{item.postingTypeNo}
    ,#{item.title}
    ,#{item.imgSrc}
    ,#{item.postingRegDt}
)
