mapper.insert:

/* 특정 자주하는 질문 삽입 쿼리 */
INSERT INTO web_app_test.tb_faq(TITLE, CONTENT)
VALUE (#{title}, #{content})


        /* 특정 자주하는 질문 삽입 쿼리 */
        SELECT MAX(FAQ_NO)
        FROM web_app_test.tb_faq

mapper.update:

/* 단일 자주하는 질문 리스트 수정 쿼리 */
UPDATE web_app_test.tb_faq
SET TITLE = #{title}
    , CONTENT = #{content}
    , USE_YN = #{useYnEnum}
    , MOD_DT = CURRENT_TIMESTAMP
WHERE FAQ_NO = #{faqNo}


/* 단일 자주하는 질문 (상태) 수정 쿼리 */
UPDATE web_app_test.tb_faq
SET USE_YN = #{useYnEnum}
  , MOD_DT = CURRENT_TIMESTAMP
WHERE FAQ_NO = #{faqNo}

mapper.delete:

/* 단일 자주하는 질문 리스트 삭제 쿼리 */
DELETE FROM web_app_test.tb_faq
WHERE FAQ_NO = #{faqNo}
