mapper.select:
/* 전체 수리 종류 리스트 조회 쿼리 */
select r_type_no as repairTypeNo , r_type_order as repairTypeOrder , title as title , explanation as explanation , reg_dt as regDt , mod_dt as modDt from web_app_test.tb_repair_type order by r_type_no desc
limit #{itemCnt}

/* 전체 수리 지역 리스트 조회 쿼리 */
select r_location_no as repairLocationNo , r_location_order as repairLocationOrder , name as name , reg_dt as regDt , mod_dt as modDt from web_app_test.tb_repair_location order by r_location_no desc
limit #{itemCnt}

/* 전체 수리 상태 리스트 조회 쿼리 */
select r_state_no as repairStateNo , name as name , explanation as explanation , reg_dt as regDt , mod_dt as modDt from web_app_test.tb_repair_state order by r_state_no desc
limit #{itemCnt}

mapper.insert:

/* 특정 견적문의 삽입 쿼리 */
insert into web_app_test.tb_repair_apply(
    r_type_no, r_location_no, r_state_no, user_type, phone_number, agree_or_not, explanation
) value(
    #{repairTypeNo}, #{repairLocationNo}, #{repairStateNo}, #{userTypeEnum}, #{phoneNumber}, #{agreeOrNotEnum}, #{explanation}
)


    /*  */
    select max(r_type_no)
    from web_app_test.tb_repair_apply

mapper.update:

/* 단일 견적 문의 수정 쿼리 */
update web_app_test.tb_repair_apply
    set r_type_no = #{repairTypeNo}
      , r_location_no = #{repairLocationNo}
      , r_state_no = #{repairStateNo}
      , user_type = #{userTypeEnum}
      , phone_number = #{phoneNumber}
      , explanation = #{explanation}
      , mod_dt = current_timestamp
where r_apply_no = #{repairApplyNo}

mapper.delete:

/* 단일 견적문의 삭제 쿼리 */
delete from web_app_test.tb_repair_apply
where r_apply_no = #{repairApplyNo}
